export const data = {
    allCars: () => {
        return new Promise((resolve, reject) => {
            cars.forEach(car => car.ownerName = owners.find(o => o.id === car.owner).name)
            resolve(cars.sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime()))
        })
    },
    getCar: (id) => {
        return new Promise((resolve, reject) => {
            let car = cars.find(car => car.id === id)
            car.ownerName = owners.find(owner => owner.id === car.owner).name
            car.comments = comments.filter(c => c.car === id)
            resolve(car)
        })
    },
    getOwners: () => {
        return new Promise((resolve, reject) => {
            owners.forEach(owner => owner.carsLength = cars.filter(c =>  c.owner === owner.id).length)
            resolve(owners)
        })
    },
    getOwner: (id) => {
        return new Promise((resolve, reject) => {
            let owner = owners.find(owner => owner.id === id)
            owner.cars = cars.filter(car => car.owner === id)
            resolve(owner)
        })
    },
    createCar: (car) => {
        return new Promise((resolve, reject) => {
            car.id = cars.length + 1
            car.date = new Date().toISOString()
            car.ownerName = ''
            car.comments = []
            car.owner = Number(car.owner)
            cars.push(car)
            resolve(car)
        })
    },
    createOwner: (owner) => {
        return new Promise((resolve, reject) => {
            owner.id = owners.length + 1
            owners.push(owner)
            resolve(owner)

        })
    },
    createComment: (car, content) => {
        return new Promise((resolve, reject) => {
            car = Number(car)
            let id = comments.length + 1
            comments.push({
                id: id,
                car: car,
                content: content
            })
            resolve(car);
        })
    },
    editCar: (updateCar) => {
        return new Promise((resolve, reject) => {
            let oldCar = cars.find(car => car.id === updateCar.id)
            oldCar.description = updateCar.description
            oldCar.engine = updateCar.engine 
            oldCar.image = updateCar.image
            oldCar.make = updateCar.make 
            oldCar.model = updateCar.model 
            oldCar.owner = Number(updateCar.owner)
            oldCar.price = updateCar.price
            resolve(oldCar)
        })
    },
   getCommentById: (id) => {
    return new Promise((resolve, reject) => {
      resolve(comments.find(c => c.id === id))
    })
  },
    editComment: (id, content) => {
        return new Promise((resolve, reject) => {
            let comment = comments.find(c => c.id === id)
            comment.content = content
            resolve(comment)
        })
    },
    editOwner: (editOwner) => {
        return new Promise((resolve, reject) => {
            let oldOwner = owners.find(owner => owner.id === editOwner.id)
            oldOwner.name = editOwner.name 
            oldOwner.image = editOwner.image
            resolve(oldOwner)
        })
    }
    
}